import { useState } from 'react';
import './App.css';
import Overview from './pages/VeilingmeesterOverview';
import CreateAuction from './pages/VeilingmeesterCreateAuction';
import Productenoverzicht from './pages/Productenoverzicht';

function App() {
    const [currentPage, setCurrentPage] = useState('overview');
    const [auctions, setAuctions] = useState([
        {
            id: 1,
            name: 'Rode Rozen Boeket',
            maxTime: 120,
            currentPrice: 45.00,
            startingPrice: 100.00,
            startTime: Date.now()
        },
        {
            id: 2,
            name: 'Tulpen Mix',
            maxTime: 90,
            currentPrice: 28.50,
            startingPrice: 60.00,
            startTime: Date.now()
        }
    ]);

    const addAuction = (auction) => {
        const newAuction = {
            ...auction,
            id: auctions.length + 1,
            currentPrice: auction.startingPrice,
            startTime: Date.now()
        };
        setAuctions([...auctions, newAuction]);
    };

    return (
        <div className="App">
            <nav className="navbar">
                <div className="nav-container">
                    <h1 className="logo">Flora Veiling</h1>
                    <ul className="nav-menu">
                        <li>
                            <a 
                                href="#overview" 
                                className={currentPage === 'overview' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    setCurrentPage('overview');
                                }}
                            >
                                Overzicht
                            </a>
                        </li>
                        <li>
                            <a 
                                href="#create" 
                                className={currentPage === 'create' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    setCurrentPage('create');
                                }}
                            >
                                Veiling Aanmaken
                            </a>
                        </li>
                        <li>
                            <a
                                href="#producten"
                                className={currentPage === 'producten' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    setCurrentPage('producten');
                                }}
                            >
                                Productenoverzicht
                            </a>
                        </li>
                        <li>
                            <a
                                href="#app"
                                className={currentPage === 'app' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    setCurrentPage('app');
                                }}
                            >
                                Welkom, Veilingmeester!
                            </a>
                        </li>
                    </ul>
                </div>
            </nav>

            <div className="content">
                {currentPage === 'overview' && (
                    <>
                        <div className="welcome-section">
                            <h2>Welkom bij Flora Veiling</h2>
                            <p>Ontdek de beste bloemen tegen de beste prijzen via ons unieke aflopende veiling systeem</p>
                        </div>
                        <Overview auctions={auctions} setAuctions={setAuctions} />
                    </>
                )}
                {currentPage === 'create' && (
                    <CreateAuction auctions={auctions} addAuction={addAuction} />
                )}
                {currentPage === 'producten' && (
                    <Productenoverzicht auctions={auctions} />
                )}
            </div>
        </div>
    );
}

export default App;
