import { useEffect, useState } from 'react';
import '../styles/Productenoverzicht.css';

function Productenoverzicht({ auctions }) {
    const formatPrice = (price) => {
        return price != null ? `€ ${price.toFixed(2)}` : '-';
    };

    const formatDate = (timestamp) => {
        if (!timestamp) return '-';
        const d = new Date(timestamp);
        return d.toLocaleString('nl-NL', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit' });
    };

    // local state for auctions loaded from backend
    const [localAuctions, setLocalAuctions] = useState([]);

    const fetchAuctions = async () => {
        try {
            const res = await fetch('http://localhost:5102/api/auctions');
            if (!res.ok) throw new Error('Fout bij ophalen veilingen');
            const data = await res.json();
            // data is expected to be an array of { id, name, maxTime, startingPrice, status }
            setLocalAuctions(data || []);
        } catch (err) {
            console.error('fetchAuctions error:', err);
            setLocalAuctions([]);
        }
    };

    // fetch on mount, on focus/visibility and poll periodically so deletions on other pages are reflected
    useEffect(() => {
        fetchAuctions();
        const onFocus = () => fetchAuctions();
        const onVisibility = () => {
            if (document.visibilityState === 'visible') fetchAuctions();
        };
        window.addEventListener('focus', onFocus);
        document.addEventListener('visibilitychange', onVisibility);
        const poll = setInterval(fetchAuctions, 30000); // every 30s
        return () => {
            window.removeEventListener('focus', onFocus);
            document.removeEventListener('visibilitychange', onVisibility);
            clearInterval(poll);
        };
    }, []);

    const rows = (localAuctions || []).map(a => {
        const ended = a.status && (a.status.toLowerCase() === 'done' || a.status.toLowerCase() === 'geveild');
        // veildatum/end time not provided by API; leave null if unknown
        const veildatum = null;
        // verkoopbedrag - backend currently returns minimum/starting price; use that as fallback
        const verkoopbedrag = ended ? (a.startingPrice ?? a.minimumPrijs ?? null) : null;
        return {
            id: a.id,
            product: a.name,
            status: ended ? 'Geveild' : 'Niet geveild',
            veildatum,
            verkoopbedrag,
            koper: ended ? '-' : '-'
        };
    });

    return (
        <div className="producten-container">
            <h1 className="producten-title">Productenoverzicht</h1>

            <div className="producten-table">
                <div className="table-header">
                    <div>Product</div>
                    <div>Status</div>
                    <div>Veildatum</div>
                    <div>Verkoopbedrag</div>
                    <div>Koper</div>
                </div>

                {rows.map(r => (
                    <div key={r.id} className="table-row">
                        <div className="cell product-cell">{r.product}</div>
                        <div className={`cell status-cell ${r.status === 'Geveild' ? 'sold' : 'unsold'}`}>{r.status}</div>
                        <div className="cell">{formatDate(r.veildatum)}</div>
                        <div className="cell">{formatPrice(r.verkoopbedrag)}</div>
                        <div className="cell">{r.koper}</div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Productenoverzicht;
